sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Construct a GlideRecord query to find all NeedIt Task records with Due date
  // field values in the past for the same Assigned to.  The records cannot be in the
  // Closed state.
  var rightNow = new GlideDateTime();
  /*
  var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
  overdueNITasks.addQuery('due_date','<=',rightNow);
  overdueNITasks.addQuery('state','<',3);
  overdueNITasks.addQuery('assigned_to',current.assigned_to);

  overdueNITasks.query();
  */

	var countoverdueNITasks = new GlideAggregate('x_58872_needit_needit_task');
	countoverdueNITasks.addQuery('due_date','<=',rightNow);
	countoverdueNITasks.addQuery('state','<',3);
	countoverdueNITasks.addQuery('assigned_to',current.assigned_to);
	countoverdueNITasks.addAggregate('COUNT');
	countoverdueNITasks.query();

	countoverdueNITasks.next();
	var num = countoverdueNITasks.getAggregate('COUNT');

  // Print to the Message field on a notification 
	if(num > 1) {
		template.print("In addition to this overdue NeedIt Task, you have " + (num - 1) + " more overdue NeedIt Tasks.");
	}

})(current, template, email, email_action, event);
